if (!document.getElementById("uninstallSidebar")) {
    let uninstallBtn = document.createElement("a");
    uninstallBtn.href = "#";
    uninstallBtn.id = "uninstallSidebar";
    uninstallBtn.textContent = "‚ùå Uninstall Sidebar";
    uninstallBtn.style.color = "red";
    uninstallBtn.style.textDecoration = "none";
    uninstallBtn.style.position = "fixed";
    uninstallBtn.style.bottom = "10px";
    uninstallBtn.style.left = "10px";
    uninstallBtn.style.fontSize = "14px";
    document.body.appendChild(uninstallBtn);

    uninstallBtn.addEventListener("click", function () {
        document.cookie = "sidebarRemoved=true; path=/; expires=Fri, 31 Dec 9999 23:59:59 GMT";
        localStorage.removeItem("sidebarVisible");
        document.getElementById("custom-sidebar")?.remove();
        document.getElementById("openSidebar")?.remove();
        alert("Sidebar uninstalled. Refresh the page.");
    });
}
